module.exports = form

function form ({ onfield, onsubmit}) {
  const el = document.createElement('div')
  el.setAttribute('class', 'form')
  el.style = `
  flex-direction: column;
  width: 500px;
  display: flex;`
  el.innerHTML = `
    <h1> generate workshop </h1>
    <input name="title" placeholder="title">
    <input name="description" placeholder="description">
    <input name="ens_domain" placeholder="ens domain">
    <button>publish</button>`
  const [title, description, ens_domain, submit] = el.children
  title.onchange = event => onfield('title', title.value)
  description.onchange = event => onfield('description', description.value)
  ens_domain.onchange = event => onfield('ens_domain', ens_domain.value)
  submit.onclick = event => onsubmit({
    title: title.value,
    ens_domain: ens_domain.value,
    description: description.value,
  })
  return el
}
